      return PostTag.insert({
        post_id: id,
        tag_id: tag._id
      });
    }).then(function(tags) {
      // Remove old tags
      var deleted = _.difference(existed, tags.map(pickID));
      return deleted;
    }).map(function(tag) {
      return PostTag.removeById(tag);
    });
  });

