'use strict';

var moment = require('moment');
var Promise = require('bluebird');
var pathFn = require('path');
var _ = require('lodash');
var yaml = require('js-yaml');
var util = require('hexo-util');
var fs = require('hexo-fs');
var yfm = require('hexo-front-matter');

var slugize = util.slugize;
var escapeRegExp = util.escapeRegExp;

var rEscapeContent = /<escape(?:[^>]*)>([\s\S]*?)<\/escape>/g;
var rSwigVar = /\{\{[\s\S]*?\}\}/g;
var rSwigComment = /\{#[\s\S]*?#\}/g;
var rSwigBlock = /\{%[\s\S]*?%\}/g;
var rSwigFullBlock = /\{% *(.+?)(?: *| +.*)%\}[\s\S]+?\{% *end\1 *%\}/g;
var placeholder = '\uFFFC';
var rPlaceholder = /(?:<|&lt;)\!--\uFFFC(\d+)--(?:>|&gt;)/g;

var preservedKeys = {
  title: true,
  slug: true,
  path: true,
  layout: true,
  date: true,
  content: true
};

function Post(context) {
  this.context = context;
}

Post.prototype.create = function(data, replace, callback) {
  if (!callback && typeof replace === 'function') {
    callback = replace;
    replace = false;
  }

  var ctx = this.context;
  var config = ctx.config;

  data.slug = slugize((data.slug || data.title).toString(), {transform: config.filename_case});
  data.layout = (data.layout || config.default_layout).toLowerCase();
  data.date = data.date ? moment(data.date) : moment();

  return Promise.all([
    // Get the post path
    ctx.execFilter('new_post_path', data, {
      args: [replace],
      context: ctx
    }),
    this._renderScaffold(data)
  ]).spread(function(path, content) {
    var result = {
      path: path,
      content: content
    };

    return Promise.all([
      // Write content to file
      fs.writeFile(path, content),
      // Create asset folder
      createAssetFolder(path, config.post_asset_folder)
    ]).then(function() {
      ctx.emit('new', result);
    }).thenReturn(result);
  }).asCallback(callback);
};

function prepareFrontMatter(data) {
  var keys = Object.keys(data);
  var key = '';
  var item;

  for (var i = 0, len = keys.length; i < len; i++) {
    key = keys[i];
    item = data[key];

    if (moment.isMoment(item)) {
      data[key] = item.utc().format('YYYY-MM-DD HH:mm:ss');
    } else if (moment.isDate(item)) {
      data[key] = moment.utc(item).format('YYYY-MM-DD HH:mm:ss');
    } else if (typeof item === 'string') {
      data[key] = '"' + item + '"';
    }
  }

  return data;
}

Post.prototype._getScaffold = function(layout) {
  var ctx = this.context;

  return ctx.scaffold.get(layout).then(function(result) {
    if (result != null) return result;
    return ctx.scaffold.get('normal');
  });
};

Post.prototype._renderScaffold = function(data) {
  var tag = this.context.extend.tag;
  var yfmSplit;

  return this._getScaffold(data.layout).then(function(scaffold) {
    var frontMatter = prepareFrontMatter(_.clone(data));
    yfmSplit = yfm.split(scaffold);

    return tag.render(yfmSplit.data, frontMatter);
  }).then(function(frontMatter) {
    var separator = yfmSplit.separator;
    var jsonMode = separator[0] === ';';
    var content = '';
    var obj;

    // Parse front-matter
    if (jsonMode) {
      obj = JSON.parse('{' + frontMatter + '}');
    } else {
      obj = yaml.load(frontMatter);
    }

    // Add data which are not in the front-matter
    var keys = Object.keys(data);
    var key = '';

    for (var i = 0, len = keys.length; i < len; i++) {
      key = keys[i];

      if (!preservedKeys[key] && obj[key] == null) {
        obj[key] = data[key];
      }
    }

    // Prepend the separator
    if (yfmSplit.prefixSeparator) content += separator + '\n';

    content += yfm.stringify(obj, {
      mode: jsonMode ? 'json' : ''
    });

    // Concat content
    content += yfmSplit.content;

    if (data.content) {
      content += '\n' + data.content;
    }

    return content;
  });
};

function createAssetFolder(path, assetFolder) {
  if (!assetFolder) return Promise.resolve();

  var target = removeExtname(path);

  return fs.exists(target).then(function(exist) {
    if (!exist) return fs.mkdirs(target);
  });
}

function removeExtname(str) {
  return str.substring(0, str.length - pathFn.extname(str).length);
}

Post.prototype.publish = function(data, replace, callback) {
  if (!callback && typeof replace === 'function') {
    callback = replace;
    replace = false;
  }

  if (data.layout === 'draft') data.layout = 'post';

  var ctx = this.context;
  var config = ctx.config;
  var draftDir = pathFn.join(ctx.source_dir, '_drafts');
  var slug = data.slug = slugize(data.slug.toString(), {transform: config.filename_case});
  var regex = new RegExp('^' + escapeRegExp(slug) + '(?:[^\\/\\\\]+)');
  var self = this;
  var src = '';
  var result = {};

  data.layout = (data.layout || config.default_layout).toLowerCase();

  // Find the draft
  return fs.listDir(draftDir).then(function(list) {
    var item = '';

    for (var i = 0, len = list.length; i < len; i++) {
      item = list[i];
      if (regex.test(item)) return item;
    }
  }).then(function(item) {
    if (!item) throw new Error('Draft "' + slug + '" does not exist.');

    // Read the content
    src = pathFn.join(draftDir, item);
    return fs.readFile(src);
  }).then(function(content) {
    // Create post
    _.extend(data, yfm(content));
    data.content = data._content;
    delete data._content;

    return self.create(data, replace).then(function(post) {
      result.path = post.path;
      result.content = post.content;
    });
  }).then(function() {
    // Remove the original draft file
    return fs.unlink(src);
  }).then(function() {
    if (!config.post_asset_folder) return;

    // Copy assets
    var assetSrc = removeExtname(src);
    var assetDest = removeExtname(result.path);

    return fs.exists(assetSrc).then(function(exist) {
      if (!exist) return;

      return fs.copyDir(assetSrc, assetDest).then(function() {
        return fs.rmdir(assetSrc);
      });
    });
  }).thenReturn(result).asCallback(callback);
};

Post.prototype.render = function(source, data, callback) {
  data = data || {};

  var ctx = this.context;
  var config = ctx.config;
  var cache = [];
  var tag = ctx.extend.tag;
  var isSwig = data.engine === 'swig' || (source && pathFn.extname(source) === '.swig');

  function escapeContent(str) {
    return '<!--' + placeholder + (cache.push(str) - 1) + '-->';
  }

  function tagFilter(content) {
    // Replace cache data with real contents
    content = content.replace(rPlaceholder, function() {
      return cache[arguments[1]];
    });

    // Render with Nunjucks
    data.content = content;
    return tag.render(data.content, data);
  }

  return new Promise(function(resolve, reject) {
    if (data.content != null) return resolve(data.content);
    if (!source) return reject(new Error('No input file or string!'));

    // Read content from files
    fs.readFile(source).then(resolve, reject);
  }).then(function(content) {
    data.content = content;

    // Run "before_post_render" filters
    return ctx.execFilter('before_post_render', data, {context: ctx}).then(function() {
      data.content = data.content.replace(rEscapeContent, function(match, content) {
        return escapeContent(content);
      });
    });
  }).then(function() {
    // Skip rendering if this is a swig file
    if (isSwig) return data.content;

    // Escape all Swig tags
    data.content = data.content
      .replace(rSwigFullBlock, escapeContent)
      .replace(rSwigBlock, escapeContent)
      .replace(rSwigComment, '')
      .replace(rSwigVar, escapeContent);

    var options = data.markdown || {};
    if (!config.highlight.enable) options.highlight = null;

    // Render with markdown or other renderer
    return ctx.render.render({
      text: data.content,
      path: source,
      engine: data.engine,
      toString: true,
      onRenderEnd: tagFilter
    }, options);
  }).then(function(content) {
    data.content = content;

    if (!isSwig) {
      return data.content;
    }

    // Render with Nunjucks
    return tag.render(data.content, data);
  }).then(function(content) {
    data.content = content;

    // Run "after_post_render" filters
    return ctx.execFilter('after_post_render', data, {context: ctx});
  });
};

module.exports = Post;

'use strict';

var yaml = require('js-yaml');
var util = require('util');
var isDate = util.isDate;
var rPrefixSep = /^(-{3,}|;{3,})/;
var rFrontMatter = /^(-{3,}|;{3,})\n([\s\S]+?)\n\1(?:$|\n([\s\S]*)$)/;
var rFrontMatterNew = /^([\s\S]+?)\n(-{3,}|;{3,})(?:$|\n([\s\S]*)$)/;

function split(str) {
  if (typeof str !== 'string') throw new TypeError('str is required!');

  if (rFrontMatter.test(str)) return splitOld(str);
  if (rFrontMatterNew.test(str)) return splitNew(str);

  return {content: str};
}

function splitOld(str) {
  var match = str.match(rFrontMatter);

  return {
    data: match[2],
    content: match[3] || '',
    separator: match[1],
    prefixSeparator: true
  };
}

function splitNew(str) {
  if (rPrefixSep.test(str)) return {content: str};

  var match = str.match(rFrontMatterNew);

  return {
    data: match[1],
    content: match[3] || '',
    separator: match[2],
    prefixSeparator: false
  };
}

function parse(str, options) {
  if (typeof str !== 'string') throw new TypeError('str is required!');

  var splitData = split(str);
  var raw = splitData.data;

  if (!raw) return {_content: str};

  var data;

  if (splitData.separator[0] === ';') {
    data = parseJSON(raw);
  } else {
    data = parseYAML(raw, options);
  }

  if (!data) return {_content: str};

  var keys = Object.keys(data);
  var key = '';
  var item;

  // Convert timezone
  for (var i = 0, len = keys.length; i < len; i++) {
    key = keys[i];
    item = data[key];

    if (isDate(item)) {
      data[key] = new Date(item.getTime() + item.getTimezoneOffset() * 60 * 1000);
    }
  }

  data._content = splitData.content;
  return data;
}

function parseYAML(str, options) {
  var result = yaml.load(escapeYAML(str), options);
  if (typeof result !== 'object') return;

  return result;
}

function parseJSON(str) {
  try {
    return JSON.parse('{' + str + '}');
  } catch (err) {
    return;
  }
}

function escapeYAML(str) {
  if (typeof str !== 'string') throw new TypeError('str is required!');

  return str.replace(/\n(\t+)/g, function(match, tabs) {
    var result = '\n';

    for (var i = 0, len = tabs.length; i < len; i++) {
      result += '  ';
    }

    return result;
  });
}

function stringify(obj, options) {
  if (!obj) throw new TypeError('obj is required!');

  options = options || {};

  var content = obj._content || '';
  delete obj._content;

  if (!Object.keys(obj).length) return content;

  var mode = options.mode;
  var separator = options.separator || (mode === 'json' ? ';;;' : '---');
  var result = '';

  if (options.prefixSeparator) result += separator + '\n';

  if (mode === 'json') {
    result += stringifyJSON(obj);
  } else {
    result += stringifyYAML(obj, options);
  }

  result += separator + '\n' + content;

  return result;
}

function stringifyYAML(obj, options) {
  var keys = Object.keys(obj);
  var data = {};
  var nullKeys = [];
  var dateKeys = [];
  var key, value, i, len;

  for (i = 0, len = keys.length; i < len; i++) {
    key = keys[i];
    value = obj[key];

    if (value == null) {
      nullKeys.push(key);
    } else if (isDate(value)) {
      dateKeys.push(key);
    } else {
      data[key] = value;
    }
  }

  var result = yaml.dump(data, options);

  if (dateKeys.length) {
    for (i = 0, len = dateKeys.length; i < len; i++) {
      key = dateKeys[i];
      result += key + ': ' + formatDate(obj[key]) + '\n';
    }
  }

  if (nullKeys.length) {
    for (i = 0, len = nullKeys.length; i < len; i++) {
      result += nullKeys[i] + ':\n';
    }
  }

  return result;
}

function stringifyJSON(obj) {
  return JSON.stringify(obj, null, '  ')
    // Remove indention
    .replace(/\n {2}/g, function() {
      return '\n';
    })
    // Remove prefixing and trailing braces
    .replace(/^{\n|}$/g, '');
}

function doubleDigit(num) {
  return num < 10 ? '0' + num : num;
}

function formatDate(date) {
  return date.getFullYear() + '-' +
    doubleDigit(date.getMonth() + 1) + '-' +
    doubleDigit(date.getDate()) + ' ' +
    doubleDigit(date.getHours()) + ':' +
    doubleDigit(date.getMinutes()) + ':' +
    doubleDigit(date.getSeconds());
}

exports = module.exports = parse;
exports.parse = parse;
exports.split = split;
exports.escape = escapeYAML;
exports.stringify = stringify;

'use strict';

var pathFn = require('path');
var fs = require('hexo-fs');

function Scaffold(context) {
  this.context = context;
  this.scaffoldDir = context.scaffold_dir;
}

Scaffold.prototype.defaults = {
  normal: [
    '---',
    'layout: {{ layout }}',
    'title: {{ title }}',
    'date: {{ date }}',
    'tags:',
    '---'
  ].join('\n')
};

Scaffold.prototype._listDir = function() {
  var scaffoldDir = this.scaffoldDir;

  return fs.exists(scaffoldDir).then(function(exist) {
    if (!exist) return [];

    return fs.listDir(scaffoldDir, {
      ignoreFilesRegex: /^_|\/_/
    });
  }).map(function(item) {
    return {
      name: item.substring(0, item.length - pathFn.extname(item).length),
      path: pathFn.join(scaffoldDir, item)
    };
  });
};

Scaffold.prototype._getScaffold = function(name) {
  return this._listDir().then(function(list) {
    var item;

    for (var i = 0, len = list.length; i < len; i++) {
      item = list[i];
      if (item.name === name) return item;
    }
  });
};

Scaffold.prototype.get = function(name, callback) {
  var self = this;

  return this._getScaffold(name).then(function(item) {
    if (item) {
      return fs.readFile(item.path);
    }

    return self.defaults[name];
  }).asCallback(callback);
};

Scaffold.prototype.set = function(name, content, callback) {
  var scaffoldDir = this.scaffoldDir;

  return this._getScaffold(name).then(function(item) {
    var path = item ? item.path : pathFn.join(scaffoldDir, name);
    if (!pathFn.extname(path)) path += '.md';

    return fs.writeFile(path, content);
  }).asCallback(callback);
};

Scaffold.prototype.remove = function(name, callback) {
  return this._getScaffold(name).then(function(item) {
    if (!item) return;

    return fs.unlink(item.path);
  }).asCallback(callback);
};

module.exports = Scaffold;

