Theme.prototype.getView = function(path) {
  // Replace backslashes on Windows
  path = path.replace(/\\/g, '/');

  var extname = pathFn.extname(path);
  var name = path.substring(0, path.length - extname.length);
  var views = this.views[name];

  if (!views) return;

  if (extname) {
    return views[extname];
  }

  return views[Object.keys(views)[0]];
};

Theme.prototype.setView = function(path, data) {
  var extname = pathFn.extname(path);
  var name = path.substring(0, path.length - extname.length);
  var views = this.views[name] = this.views[name] || {};

  views[extname] = new this.View(path, data);
};

Theme.prototype.removeView = function(path) {
  var extname = pathFn.extname(path);
  var name = path.substring(0, path.length - extname.length);
  var views = this.views[name];

  if (!views) return;

  delete views[extname];
};

module.exports = Theme;

'use strict';

var pathFn = require('path');
var _ = require('lodash');
var yfm = require('hexo-front-matter');
var Promise = require('bluebird');

function View(path, data) {
  this.path = path;
  this.source = pathFn.join(this._theme.base, 'layout', path);
  this.data = typeof data === 'string' ? yfm(data) : data;

  this._precompile();
}

View.prototype.render = function(options, callback) {
  if (!callback && typeof options === 'function') {
    callback = options;
    options = {};
  }

  options = options || {};

  var data = this.data;
  var layout = data.hasOwnProperty('layout') ? data.layout : options.layout;
  var locals = this._buildLocals(options);
  var self = this;

  return this._compiled(this._bindHelpers(locals)).then(function(result) {
    if (result == null || !layout) return result;

    var layoutView = self._resolveLayout(layout);
    if (!layoutView) return result;

    var layoutLocals = _.assign({}, locals, {
      body: result,
      layout: false
    });

    return layoutView.render(layoutLocals, callback);
  }).asCallback(callback);
};

View.prototype.renderSync = function(options) {
  options = options || {};

  var data = this.data;
  var layout = data.hasOwnProperty('layout') ? data.layout : options.layout;
  var locals = this._buildLocals(options);
  var result = this._compiledSync(this._bindHelpers(locals));

  if (result == null || !layout) return result;

  var layoutView = this._resolveLayout(layout);
  if (!layoutView) return result;

  var layoutLocals = _.assign({}, locals, {
    body: result,
    layout: false
  });

  return layoutView.renderSync(layoutLocals);
};

View.prototype._buildLocals = function(locals) {
  return _.assignIn({}, locals, _.omit(this.data, ['layout', '_content']), {
    filename: this.source
  });
};

View.prototype._bindHelpers = function(locals) {
  var helpers = this._helper.list();
  var keys = Object.keys(helpers);
  var key = '';

  for (var i = 0, len = keys.length; i < len; i++) {
    key = keys[i];
    locals[key] = _.bind(helpers[key], locals);
  }

  return locals;
};

View.prototype._resolveLayout = function(name) {
  // Relative path
  var layoutPath = pathFn.join(pathFn.dirname(this.path), name);
  var layoutView = this._theme.getView(layoutPath);

  if (layoutView && layoutView.source !== this.source) return layoutView;

  // Absolute path
  layoutView = this._theme.getView(name);
  if (layoutView && layoutView.source !== this.source) return layoutView;
};

View.prototype._precompile = function() {
  var render = this._render;
  var ctx = render.context;
  var ext = pathFn.extname(this.path);
  var renderer = render.getRenderer(ext);
  var data = {
    path: this.source,
    text: this.data._content
  };

  function buildFilterArguments(result) {
    var output = render.getOutput(ext) || ext;
    return [
      'after_render:' + output,
      result,
      {
        context: ctx,
        args: [data]
      }
    ];
  }

  if (renderer && typeof renderer.compile === 'function') {
    var compiled = renderer.compile(data);

    this._compiledSync = function(locals) {
      var result = compiled(locals);
      return ctx.execFilterSync.apply(ctx, buildFilterArguments(result));
    };

    this._compiled = function(locals) {
      return Promise.resolve(compiled(locals))
        .then(function(result) {
          return ctx.execFilter.apply(ctx, buildFilterArguments(result));
        });
    };
  } else {
    this._compiledSync = function(locals) {
      return render.renderSync(data, locals);
    };

    this._compiled = function(locals) {
      return render.render(data, locals);
    };
  }
};

module.exports = View;

'use strict';

var vsprintf = require('sprintf-js').vsprintf;

function i18n(options){
  /* jshint validthis: true */
  options = options || {};

  this.data = {};
  this.languages = options.languages || ['default'];

  if (!Array.isArray(this.languages)){
    this.languages = [this.languages];
  }
}

i18n.prototype.get = function(languages){
  var data = this.data;
  var result = {};
  var lang, langData, keys, key, i, leni, j, lenj;

  if (languages){
    if (!Array.isArray(languages)){
      languages = [languages];
    }
  } else {
    languages = this.languages;
  }

  for (i = 0, leni = languages.length; i < leni; i++){
    lang = languages[i];
    langData = data[lang];
    if (!langData) continue;

    keys = Object.keys(langData);

    for (j = 0, lenj = keys.length; j < lenj; j++){
      key = keys[j];
      if (!result.hasOwnProperty(key)) result[key] = langData[key];
    }
  }

  return result;
};

i18n.prototype.set = function(lang, data){
  if (typeof lang !== 'string') throw new TypeError('lang must be a string!');
  if (typeof data !== 'object') throw new TypeError('data is required!');

  this.data[lang] = flattenObject(data);

  return this;
};

i18n.prototype.remove = function(lang){
  if (typeof lang !== 'string') throw new TypeError('lang must be a string!');

  delete this.data[lang];

  return this;
};

i18n.prototype.list = function(){
  return Object.keys(this.data);
};

function flattenObject(data, obj, parent){
  obj = obj || {};
  parent = parent || '';

  var keys = Object.keys(data);
  var key = '';
  var item;

  for (var i = 0, len = keys.length; i < len; i++){
    key = keys[i];
    item = data[key];

    if (typeof item === 'object'){
      flattenObject(item, obj, parent + key + '.');
    } else {
      obj[parent + key] = item;
    }
  }

  return obj;
}

i18n.prototype.__ = function(lang){
  var data = this.get(lang);

  return function(){
    var len = arguments.length;
    if (!len) return '';

    var args = new Array(len);

    for (var i = 0; i < len; i++){
      args[i] = arguments[i];
    }

    var key = args.shift();
    var str = data[key] || key;

    return vsprintf(str, args);
  };
};

i18n.prototype._p = function(lang){
  var data = this.get(lang);

  return function(){
    var len = arguments.length;
    if (!len) return '';

    var args = new Array(len);

    for (var i = 0; i < len; i++){
      args[i] = arguments[i];
    }

    var key = args.shift();
    var number = args.length ? +args[0] : 0;
    var str = key;

    if (!number && data.hasOwnProperty(key + '.zero')){
      str = data[key + '.zero'];
    } else if (number === 1 && data.hasOwnProperty(key + '.one')){
      str = data[key + '.one'];
    } else if (data.hasOwnProperty(key + '.other')){
      str = data[key + '.other'];
    } else if (data.hasOwnProperty(key)){
      str = data[key];
    }

    return vsprintf(str, args);
  };
};

module.exports = i18n;

'use strict';

function Locals() {
  this.cache = {};
  this.getters = {};
}

Locals.prototype.get = function(name) {
  if (typeof name !== 'string') throw new TypeError('name must be a string!');

  var cache = this.cache[name];

  if (cache == null) {
    var getter = this.getters[name];
    if (!getter) return;

    cache = this.cache[name] = getter();
  }

  return cache;
};

Locals.prototype.set = function(name, value) {
  if (typeof name !== 'string') throw new TypeError('name must be a string!');
  if (value == null) throw new TypeError('value is required!');

  var getter;

  if (typeof value === 'function') {
    getter = value;
  } else {
    getter = function() {
      return value;
    };
  }

  this.getters[name] = getter;
  this.cache[name] = null;

  return this;
};

Locals.prototype.remove = function(name) {
  if (typeof name !== 'string') throw new TypeError('name must be a string!');

  this.getters[name] = null;
  this.cache[name] = null;

  return this;
};

Locals.prototype.invalidate = function() {
  this.cache = {};

  return this;
};

Locals.prototype.toObject = function() {
  var result = {};
  var keys = Object.keys(this.getters);
  var key = '';
  var item;

  for (var i = 0, len = keys.length; i < len; i++) {
    key = keys[i];
    item = this.get(key);

    if (item != null) result[key] = item;
  }

  return result;
};

module.exports = Locals;

'use strict';

var fs = require('hexo-fs');
var Promise = require('bluebird');

module.exports = function(ctx) {
  if (ctx._dbLoaded) return Promise.resolve();

  var db = ctx.database;
  var path = db.options.path;
  var log = ctx.log;

  return fs.exists(path).then(function(exist) {
    if (!exist) return;

    log.debug('Loading database.');
    return db.load();
  }).then(function() {
    ctx._dbLoaded = true;
  }).catch(function() {
    log.error('Database load failed. Deleting database.');
    return fs.unlink(path);
  });
};
